-- Records of tb_token
-- ----------------------------


-- ----------------------------
-- Table structure for tb_user
-- ----------------------------
DROP TABLE IF EXISTS `tb_user`;
CREATE TABLE `tb_user` (
  `id` bigint NOT NULL AUTO_INCREMENT COMMENT '用户id(主键)',
  `mobile` varchar(255) CHARACTER SET utf8 COLLATE utf8_bin DEFAULT NULL COMMENT '手机号',
  `wx_openid` varchar(255) CHARACTER SET utf8 COLLATE utf8_bin DEFAULT NULL COMMENT '普通用户的标识，对当前开发者帐号唯一',
  `wx_unionId` varchar(255) CHARACTER SET utf8 COLLATE utf8_bin DEFAULT NULL COMMENT '用户统一标识。针对一个微信开放平台帐号下的应用，同一用户的unionid是唯一的',
  `create_time` datetime DEFAULT NULL COMMENT '创建时间',
  `vip_date` datetime DEFAULT NULL COMMENT 'VIP到期时间',
  `last_login` datetime DEFAULT NULL COMMENT '最后登录时间',
  `status` int DEFAULT NULL COMMENT '0:正常状态 1:封禁状态',
  `lock_reason` varchar(140) CHARACTER SET utf8 COLLATE utf8_bin DEFAULT NULL COMMENT '封禁原因',
  `nick_name` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin DEFAULT NULL COMMENT '昵称',
  `avatar` varchar(255) CHARACTER SET utf8 COLLATE utf8_bin DEFAULT NULL COMMENT '头像地址',
  `ip` varchar(22) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT NULL COMMENT 'ip',
  `channel` varchar(22) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT NULL COMMENT '渠道号',
  `username` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin DEFAULT NULL COMMENT '账号(可以为空但是不能重复)',
  `password` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin DEFAULT NULL,
  `pid` bigint DEFAULT NULL COMMENT '上级ID',
  `msg_count` int DEFAULT NULL COMMENT '剩余消息次数',
  `money` decimal(20,2) DEFAULT '0.00' COMMENT '用户可提现余额',
  `levelone_proportion` decimal(20,2) DEFAULT NULL COMMENT '用户下一级返利比例',
  `secondary_proportion` decimal(20,2) DEFAULT NULL COMMENT '用户下二级返利比例',
  `gpid` bigint DEFAULT NULL COMMENT '分销最顶级上级ID',
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE KEY `wy` (`mobile`,`username`) USING BTREE,
  KEY `sy` (`status`,`nick_name`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=1651568044629045250 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_bin COMMENT='用户表';

-- ----------------------------
-- Records of tb_user
-- ----------------------------


-- ----------------------------
-- Table structure for tb_withdrawal
-- ----------------------------
DROP TABLE IF EXISTS `tb_withdrawal`;
CREATE TABLE `tb_withdrawal` (
  `id` bigint NOT NULL AUTO_INCREMENT COMMENT '提现表ID（主键ID）',
  `user_id` bigint NOT NULL COMMENT '用户id',
  `user_name` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '提现用户昵称',
  `withdrawal_money` decimal(10,2) NOT NULL COMMENT '提现金额',
  `reality_money` decimal(10,2) DEFAULT NULL COMMENT '实到金额(扣除手续费后金额)',
  `front_vacancies` decimal(10,2) DEFAULT NULL COMMENT '提现前余额',
  `behind_vacancies` decimal(10,2) DEFAULT NULL COMMENT '提现后余额(当前余额)',
  `service_charge` decimal(10,2) DEFAULT NULL COMMENT '提现手续费',
  `service_charge_scale` decimal(10,2) DEFAULT NULL COMMENT '提现手续费比例',
  `make_collections_url` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL COMMENT '用户收款二维码链接',
  `type` int DEFAULT NULL COMMENT '收款类型(1:支付宝  2:微信)',
  `status` int DEFAULT NULL COMMENT '审核状态(0:审核中  1:审核通过  2:审核拒绝)',
  `content` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL COMMENT '审核内容(拒绝填写拒绝理由  成功不用填写)',
  `time` datetime DEFAULT NULL COMMENT '创建时间',
  `examine_time` datetime DEFAULT NULL COMMENT '审核时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=1651476441935642627 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci COMMENT='用户提现表';

